 #{ userId,jdbcType=VARCHAR}

mapper.select:
select

from system_user
where 1=1

    AND user_id =  #{ userId,jdbcType=
    VARCHAR }


    AND user_name =  #{ userName,jdbcType=
    VARCHAR }


    AND password =  #{ password,jdbcType=
    VARCHAR }


    AND real_name =  #{ realName,jdbcType=
    VARCHAR }


    AND department_id =  #{ departmentId,jdbcType= BIGINT }


    AND user_number =  #{ userNumber,jdbcType=
    VARCHAR }


    AND phone =  #{ phone,jdbcType=
    VARCHAR }


    AND email =  #{ email,jdbcType=
    VARCHAR }


    AND create_time =  #{ createTime,jdbcType=
    VARCHAR }


    AND del_flag =  #{ delFlag,jdbcType=
    VARCHAR }


    AND department_leader =  #{ departmentLeader,jdbcType=
    VARCHAR }


    AND charge_leader =  #{ chargeLeader,jdbcType=
    VARCHAR }


    AND user_position =  #{ userPosition,jdbcType=
    VARCHAR }


    AND create_user_id =  #{ createUserId,jdbcType= BIGINT }


    AND update_time =  #{ updateTime,jdbcType= BIGINT }

ORDER BY user_id desc

